$url = 'https://example.com/feed.rss';\n$xml = simplexml_load_file($url);\n\nif ($xml === false) {\n    echo "Failed loading XML\n";\n    foreach(libxml_get_errors() as $error) {\n        echo "\t", $error->message;\n    }\n} else {\n    foreach ($xml->channel->item as $item) {\n        echo $item->title . "\n";\n        echo $item->link . "\n";\n        echo $item->description . "\n\n";\n    }\n}