class Foo\n{\n    public function __construct()\n    {\n        $bar = new Bar();\n        $bar->test();\n    }\n}\n\nclass Bar\n{\n    public function test()\n    {\n        $backtrace = debug_backtrace();\n        $caller = $backtrace[1];\n        \n        echo "Called by class: " . $caller['class'] . "\n";\n        echo "Called by method: " . $caller['function'] . "\n";\n    }\n}\n\n$foo = new Foo();