function getVariableName(&$var, $scope = null) {\n    $scope = $scope == null ? $GLOBALS : $scope; // if no scope is provided, default to global scope\n    $tmp = $var; // store the variable value\n    $var = uniqid(); // set the variable to a unique value\n    $name = array_search($var, $scope, true); // search for the unique value in the given scope\n    $var = $tmp; // restore the original variable value\n    return $name;\n}\n\n$FooBar = "a string";\necho getVariableName($FooBar); // prints: FooBar