<?php\n// Start memory usage\n$startMemory = memory_get_usage();\n\n// Your script operations here...\n\n// End memory usage\n$endMemory = memory_get_usage();\n\n// Calculate memory usage\n$memoryUsed = $endMemory - $startMemory;\n\n// Convert to KB and MB\n$memoryUsedKB = $memoryUsed / 1024;\n$memoryUsedMB = $memoryUsed / (1024 * 1024);\n\necho "Memory used: $memoryUsed bytes\n";\necho "Memory used: $memoryUsedKB KB\n";\necho "Memory used: $memoryUsedMB MB\n";\n?>