function removeGetParameter($url, $param) {\n    // Parse the URL into components\n    $parsed_url = parse_url($url);\n    \n    // Parse the query string into an associative array\n    parse_str($parsed_url['query'], $query_array);\n    \n    // Remove the specific query parameter\n    unset($query_array[$param]);\n    \n    // Build the query string again\n    $new_query = http_build_query($query_array);\n    \n    // Reconstruct the URL without the specific GET parameter\n    $new_url = $parsed_url['scheme'] . '://' . $parsed_url['host'] . $parsed_url['path'];\n    if (!empty($new_query)) {\n        $new_url .= '?' . $new_query;\n    }\n    \n    return $new_url;\n}\n\n// Example usage\n$current_url = "http://example.com/page.php?param1=value1&param2=value2";\n$modified_url = removeGetParameter($current_url, 'param1');\necho $modified_url; // Output: http://example.com/page.php?param2=value2