<?php\n\n// Read the content of the file into an array of lines\n$lines = file('uglynumber.txt', FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n\n// Define the regular expression for matching and extracting the numbers\n$regex = '/[, ]/'; // First regex to remove commas and spaces\n$numberRegex = '/.*([0-9]{9}).*/'; // Second regex to extract the 9-digit number\n\nforeach ($lines as $line) {\n    // Remove commas and spaces\n    $cleanedLine = preg_replace($regex, '', $line);\n    \n    // Extract the 9-digit number\n    if (preg_match($numberRegex, $cleanedLine, $matches)) {\n        echo $matches[1] . "\n";\n    }\n}\n\n?>