<ul id="handles" class="tabs">\n    <?php for ($i = 0, $n = count($list); $i < $n; $i++) : ?>\n        <?php \n            $item = $list[$i];\n            list($year, $month, $day) = explode("-", $item->created);\n            $tabdate = date('d/m/y', mktime(0, 0, 0, $month, $day, $year));\n        ?>\n        <?php if ($i == 0) : ?>\n            <li class="first-item"><a href="#tab"><span><?php echo 'Shout ' . $tabdate; ?></span><b></b></a></li>\n        <?php else : ?>\n            <?php modSankeSlideHelper::getTabs($item); ?>\n        <?php endif; ?>\n    <?php endfor; ?>                \n    <li class="end"></li>\n</ul>