<?php\n// Example function to create a node via XML-RPC in Drupal\nfunction create_node($title, $body, $taxonomy) {\n    $xmlrpc_url = 'https://www.example.com/xmlrpc.php';\n    $username = 'your_username';\n    $password = 'your_password';\n    \n    $request = xmlrpc_encode_request('blogger.newPost', array(\n        'appkey' => '',\n        'username' => $username,\n        'password' => $password,\n        'content' => array(\n            'title' => $title,\n            'body' => $body,\n            'taxonomy' => $taxonomy\n        ),\n        'publish' => true\n    ));\n\n    $context = stream_context_create(array('http' => array(\n        'method' => "POST",\n        'header' => "Content-Type: text/xml",\n        'content' => $request\n    )));\n\n    $file = file_get_contents($xmlrpc_url, false, $context);\n    $response = xmlrpc_decode($file);\n\n    if (xmlrpc_is_fault($response)) {\n        // Handle error\n        echo "XML-RPC Error: " . $response['faultString'];\n    } else {\n        // Success\n        echo "Node created with ID: " . $response;\n    }\n}\n\n// Example usage\ncreate_node('Sample Title', 'This is a sample body.', array('term1', 'term2'));\n?>