session_start();\n\n   if (!isset($_SESSION['downloaded'])) {\n       // Increment the download counter\n       $file = 'counter.txt';\n       $count = file_get_contents($file);\n       $count = trim($count);\n       $count = (int)$count + 1;\n       file_put_contents($file, $count);\n\n       // Mark this session as having downloaded the file\n       $_SESSION['downloaded'] = true;\n\n       // Redirect to the actual file\n       header("Location: /path-to/actual-file.zip");\n       exit;\n   } else {\n       echo "You have already downloaded this file.";\n   }