function foo1($a, $b) {\n    return $a / $b;\n}\n\nclass Bar\n{\n    static function foo2($a, $b) {\n        return $a / $b;\n    }\n\n    public function UseReferences()\n    {\n        // WORKS FINE:\n        $fn = 'foo1';\n        print $fn(1, 1);\n\n        // WORKS FINE:\n        print self::foo2(2, 1);\n        print Bar::foo2(3, 1);\n\n        // WORKS WITH ARRAY SYNTAX:\n        $fn = ['self', 'foo2'];\n        print $fn(4, 1);\n\n        $fn = ['Bar', 'foo2'];\n        print $fn(5, 1);\n    }\n}\n\n$x = new Bar();\n$x->UseReferences();