$list[0][0] = "2009-09-09";\n$list[0][1] = "2009-05-05";\n$list[0][2] = "2009-09-09";\n$list[1][0] = "first-paid";\n$list[1][1] = "1";\n$list[1][2] = "last-unpaid";\n\nfunction find_duplicate_keys($array) {\n    $value_count = array_count_values($array);\n    $duplicates = array_filter($value_count, function($count) {\n        return $count > 1;\n    });\n\n    $duplicate_keys = [];\n    foreach ($duplicates as $value => $count) {\n        foreach ($array as $key => $item) {\n            if ($item == $value) {\n                $duplicate_keys[] = $key;\n            }\n        }\n    }\n\n    return $duplicate_keys;\n}\n\n$duplicate_keys = find_duplicate_keys($list[0]);\nprint_r($duplicate_keys);