function getPagination($currentPage, $totalPages) {\n    $pagination = [];\n    $adjacentPages = 2;\n\n    // Always show the first page\n    if ($currentPage > 1) {\n        $pagination[] = 1;\n    }\n\n    // Show leading ellipsis if current page is far from the start\n    if ($currentPage > $adjacentPages + 2) {\n        $pagination[] = '...';\n    }\n\n    // Calculate start and end range of pages\n    $start = max(2, $currentPage - $adjacentPages);\n    $end = min($totalPages - 1, $currentPage + $adjacentPages);\n\n    // Add the range of pages around the current page\n    for ($i = $start; $i <= $end; $i++) {\n        $pagination[] = $i;\n    }\n\n    // Show trailing ellipsis if current page is far from the end\n    if ($currentPage < $totalPages - $adjacentPages - 1) {\n        $pagination[] = '...';\n    }\n\n    // Always show the last page\n    if ($currentPage < $totalPages) {\n        $pagination[] = $totalPages;\n    }\n\n    return $pagination;\n}\n\n// Example usage\n$currentPage = 5;\n$totalPages = 11;\n$pagination = getPagination($currentPage, $totalPages);\n\necho 'Pages: ';\nforeach ($pagination as $page) {\n    if ($page == $currentPage) {\n        echo "[$page] ";\n    } else {\n        echo "$page ";\n    }\n}