<?php\nfunction getFormattedMicrotime() {\n    // Get the current Unix timestamp with microseconds\n    $microtime = microtime(true);\n    \n    // Separate the microseconds and the seconds\n    $microseconds = sprintf("%06d", ($microtime - floor($microtime)) * 1000000);\n    $date = new DateTime(date('Y-m-d H:i:s.' . $microseconds, $microtime));\n    \n    // Format the date including microseconds\n    return $date->format("Y-m-d H:i:s.u");\n}\n\n// Example usage\necho getFormattedMicrotime();\n?>