<?php\n// Database connection details\n$host = 'localhost';\n$user = 'your_username';\n$password = 'your_password';\n\n// Connect to the first database\n$database1 = 'database1';\n$conn1 = new mysqli($host, $user, $password, $database1);\n\n// Check connection for the first database\nif ($conn1->connect_error) {\n    die("Connection failed: " . $conn1->connect_error);\n}\n\n// Connect to the second database\n$database2 = 'database2';\n$conn2 = new mysqli($host, $user, $password, $database2);\n\n// Check connection for the second database\nif ($conn2->connect_error) {\n    die("Connection failed: " . $conn2->connect_error);\n}\n\n// Example query from the first database\n$result1 = $conn1->query("SELECT * FROM table_in_database1");\nwhile ($row1 = $result1->fetch_assoc()) {\n    // Process data from the first database\n    echo "Data from database1: " . $row1['column_name'] . "<br>";\n\n    // Example insert into the second database\n    $stmt2 = $conn2->prepare("INSERT INTO table_in_database2 (column_name) VALUES (?)");\n    $stmt2->bind_param("s", $row1['column_name']);\n    $stmt2->execute();\n}\n\n// Close the connections\n$conn1->close();\n$conn2->close();\n?>