<?php\n// Define your functions\nfunction posts() {\n    echo "This is the posts function.";\n}\n\nfunction view() {\n    echo "This is the view function.";\n}\n\n// Get the URI and remove leading/trailing slashes\n$uri = trim($_SERVER['REQUEST_URI'], '/');\n\n// Split the URI into parts\n$uriParts = explode('/', $uri);\n\n// Define a map of URIs to functions\n$routes = [\n    'dev/view/posts' => 'posts',\n    'dev/view' => 'view',\n    // Add more routes as needed\n];\n\n// Construct the route key from URI parts\n$routeKey = implode('/', $uriParts);\n\n// Check if the route exists and call the corresponding function\nif (array_key_exists($routeKey, $routes) && function_exists($routes[$routeKey])) {\n    call_user_func($routes[$routeKey]);\n} else {\n    echo "404 Not Found";\n}\n?>