<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get the search term from the user input\n$searchTerm = $_POST['search_term'];\n$userId = isset($_SESSION['user_id']) ? $_SESSION['user_id'] : NULL; // Assuming you have user session management\n$userIp = $_SERVER['REMOTE_ADDR'];\n\n// Prepare and bind\n$stmt = $conn->prepare("INSERT INTO searchterms (terms, user_id, user_ip) VALUES (?, ?, ?)");\n$stmt->bind_param("sis", $searchTerm, $userId, $userIp);\n\n// Execute the statement\nif ($stmt->execute()) {\n    echo "Search term logged successfully.";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n// Close connection\n$stmt->close();\n$conn->close();\n?>