// Example test class\nclass InputSanitizerTest extends PHPUnit\Framework\TestCase\n{\n    public function testSanitizeGetInput()\n    {\n        $_GET['param'] = '<script>alert("xss")</script>';\n        \n        $sanitizer = new InputSanitizer();\n        $result = $sanitizer->sanitize($_GET['param']);\n        \n        $this->assertEquals('alert("xss")', $result);\n    }\n\n    public function testSanitizePostInput()\n    {\n        $_POST['param'] = '<script>alert("xss")</script>';\n        \n        $sanitizer = new InputSanitizer();\n        $result = $sanitizer->sanitize($_POST['param']);\n        \n        $this->assertEquals('alert("xss")', $result);\n    }\n}