<?php\n// Define the Python script and any arguments\n$pythonScript = 'path/to/your_script.py';\n$arg1 = escapeshellarg('argument1');\n$arg2 = escapeshellarg('argument2');\n\n// Prepare the command\n$cmd = "python3 $pythonScript $arg1 $arg2";\n\n// Define the descriptor specification array\n$descriptorspec = array(\n    0 => array("pipe", "r"),  // stdin\n    1 => array("pipe", "w"),  // stdout\n    2 => array("pipe", "w")   // stderr\n);\n\n$process = proc_open($cmd, $descriptorspec, $pipes);\n\nif (is_resource($process)) {\n    // Close the stdin pipe since we don't need it\n    fclose($pipes[0]);\n    \n    // Read the output from stdout and stderr\n    $output = stream_get_contents($pipes[1]);\n    fclose($pipes[1]);\n    \n    $error = stream_get_contents($pipes[2]);\n    fclose($pipes[2]);\n\n    // Close the process\n    $return_value = proc_close($process);\n\n    // Output the results\n    echo "Output: " . htmlspecialchars($output) . "<br>";\n    echo "Error: " . htmlspecialchars($error) . "<br>";\n    echo "Return value: " . $return_value;\n} else {\n    echo "Failed to start the process.";\n}\n?>