<?php\n\nclass PluginManager {\n    private $plugins = [];\n\n    public function loadPlugins($directory) {\n        foreach (glob($directory . '/*', GLOB_ONLYDIR) as $pluginDir) {\n            $manifest = json_decode(file_get_contents($pluginDir . '/manifest.json'), true);\n            if ($manifest) {\n                require_once $pluginDir . '/' . $manifest['name'] . '.php';\n                $pluginClass = $manifest['name'];\n                $this->plugins[$pluginClass] = new $pluginClass();\n            }\n        }\n    }\n\n    public function executePlugins() {\n        foreach ($this->plugins as $plugin) {\n            if (method_exists($plugin, 'execute')) {\n                $plugin->execute();\n            }\n        }\n    }\n}