<?php\n// Initialize the XML string with a root element\n$xmlString = '<?xml version="1.0" encoding="UTF-8"?><root></root>';\n\n// Create the SimpleXMLElement object\n$xml = new SimpleXMLElement($xmlString);\n\n// Add a child element\n$child = $xml->addChild('child', 'This is a child element');\n\n// Add another child element with an attribute\n$anotherChild = $xml->addChild('anotherChild', 'This is another child element');\n$anotherChild->addAttribute('attribute', 'value');\n\n// Print the XML\necho $xml->asXML();\n?>