<?php\nfunction function_autoloader($function_name) {\n    // Define the mapping of function prefixes to file names\n    $prefix_map = [\n        'html_' => 'html_functions.php',\n        'sql_' => 'sql_functions.php',\n        'date_' => 'date_functions.php',\n        // Add more mappings as necessary\n    ];\n\n    foreach ($prefix_map as $prefix => $file) {\n        if (strpos($function_name, $prefix) === 0) {\n            require_once $file;\n            return;\n        }\n    }\n}\n\n// Register the autoloader function\nspl_autoload_register(function($class_name) {\n    // Autoload classes\n    require_once $class_name . '.class.php';\n});\n\n// Register the function autoloader\nset_error_handler(function ($errno, $errstr) {\n    if (preg_match('/^Call to undefined function (\w+)\(\)$/', $errstr, $matches)) {\n        function_autoloader($matches[1]);\n        // Retry calling the function if it was successfully loaded\n        if (function_exists($matches[1])) {\n            return true;\n        }\n    }\n    return false;\n}, E_RECOVERABLE_ERROR);\n\n// Example function call\nhtml_insert_button();\n?>