$dbconn = pg_connect("host=localhost port=5432 dbname=masi user=masi password=123");\n\n// Function to check if a prepared statement exists\nfunction preparedStatementExists($dbconn, $stmtname) {\n    $result = pg_query($dbconn, "SELECT name FROM pg_prepared_statements WHERE name = '$stmtname'");\n    return (pg_num_rows($result) > 0);\n}\n\n$stmtname = "query11";\n\nif (!preparedStatementExists($dbconn, $stmtname)) {\n    $result = pg_prepare($dbconn, $stmtname, "SELECT passhash_md5 FROM users WHERE email=$1;");\n    if (!$result) {\n        throw new Exception('Prepared statement creation failed.');\n    }\n}\n\n$passhash_md5 = pg_execute($dbconn, $stmtname, array($_POST['email']));\n\nif (!$passhash_md5) {\n    echo "Execute failed: " . pg_last_error($dbconn);\n} else {\n    // Process the results\n    while ($row = pg_fetch_assoc($passhash_md5)) {\n        echo "Password hash: " . $row['passhash_md5'];\n    }\n}