$attachment = new Model_Attachment();\n$attachment->name = $info['FileName'];\n$attachment->size = strlen($info['Data']);\n$attachment->data = DB::expr('UNHEX(:data)');\n$attachment->mime_type = $info['content-type'];\n$attachment->save();\n\n// Bind the binary data to the query\nDB::query(Database::UPDATE, 'UPDATE attachments SET data = UNHEX(:data) WHERE id = :id')\n    ->parameters(array(\n        ':data' => bin2hex($info['Data']),\n        ':id' => $attachment->id,\n    ))\n    ->execute();