<?php\n// Load the JSON file containing ALT texts\n$altTexts = json_decode(file_get_contents('alt_texts.json'), true);\n\n// Function to render an image with ALT text\nfunction renderImage($src) {\n    global $altTexts;\n    $altText = isset($altTexts[basename($src)]) ? htmlspecialchars($altTexts[basename($src)]) : 'Default alt text';\n    echo '<img src="' . htmlspecialchars($src) . '" alt="' . $altText . '">';\n}\n\n// Example usage\nrenderImage('images/image1.jpg');\nrenderImage('images/image2.png');\nrenderImage('images/image3.gif');\n?>