function generateUserId($userId, $createdTimestamp) {\n    $salt = 'your_salt_value'; // Make sure this is a secure, unique salt value\n    $randomValue = bin2hex(random_bytes(16)); // 16 bytes = 128 bits of entropy\n    return hash('sha256', $salt . $userId . $createdTimestamp . $randomValue);\n}\n\n// Usage\n$userId = 1; // Replace with your auto-increment primary key\n$createdTimestamp = time(); // Replace with your created timestamp\n$uniqueUserId = generateUserId($userId, $createdTimestamp);\necho $uniqueUserId;