<?php\n\nfunction parseTextFile($text) {\n    // Convert bold text\n    $text = preg_replace('/\[b\](.*?)\[\/b\]/', '<b>$1</b>', $text);\n    // Convert italic text\n    $text = preg_replace('/\[i\](.*?)\[\/i\]/', '<i>$1</i>', $text);\n\n    // Convert unordered lists\n    $text = preg_replace_callback('/(?:^- .+\n)+/m', function ($matches) {\n        $items = explode("\n", trim($matches[0]));\n        $listItems = '';\n        foreach ($items as $item) {\n            $listItems .= '<li>' . substr($item, 2) . '</li>';\n        }\n        return "<ul>\n" . $listItems . "\n</ul>\n";\n    }, $text);\n    \n    // Convert ordered lists\n    $text = preg_replace_callback('/(?:^# .+\n)+/m', function ($matches) {\n        $items = explode("\n", trim($matches[0]));\n        $listItems = '';\n        foreach ($items as $item) {\n            $listItems .= '<li>' . substr($item, 2) . '</li>';\n        }\n        return "<ol>\n" . $listItems . "\n</ol>\n";\n    }, $text);\n\n    return $text;\n}\n\n$textFileContent = "[b]Some bold text[/b]\n[i]Italic[/i]\n- List item 1\n- List item 2\n- List item 3\n# List item 1\n# List item 2\n# List item 3\n";\n$parsedContent = parseTextFile($textFileContent);\necho nl2br(htmlspecialchars($parsedContent));\n\n?>