class Dog_Exception extends Exception {\n    public function __toString() {\n        return ''; // This will make the exception evaluate to false in a boolean context\n    }\n}\n\nclass Dog {\n    public function lookAround() {\n        if ($this->seeDog()) {\n            return 'bark';\n        } else {\n            return 'sit';\n        }\n    }\n\n    public function seeDog() {\n        $object = 'notDog'; // Example variable for demonstration\n\n        if ($object == 'Dog') {\n            return 'husky';\n        } else {\n            return new Dog_Exception('no_see');\n        }\n    }\n}\n\n// Example usage:\n$dog = new Dog();\necho $dog->lookAround(); // Outputs "sit" because Dog_Exception evaluates to false