class Application_Model_Combined\n{\n    protected $tableA;\n    protected $tableB;\n\n    public function __construct()\n    {\n        $this->tableA = new Application_Model_DbTable_A();\n        $this->tableB = new Application_Model_DbTable_B();\n    }\n\n    public function getCombinedData()\n    {\n        $select = $this->tableA->select()\n            ->setIntegrityCheck(false)\n            ->from(array('a' => 'table_a'))\n            ->join(array('b' => 'table_b'), 'a.id = b.a_id', array('b.column1', 'b.column2'))\n            ->where('a.some_column = ?', 'some_value');\n\n        return $this->tableA->fetchAll($select);\n    }\n}