<?php\nif (isset($_GET["qu"])) {\n    $query = urlencode($_GET["qu"]);\n    $url = "http://www.google.com/complete/search?hl=en&js=true&qu=" . $query;\n\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($ch, CURLOPT_TIMEOUT, 30);\n\n    $response = curl_exec($ch);\n\n    if (curl_errno($ch)) {\n        echo 'Error:' . curl_error($ch);\n    } else {\n        echo $response;\n    }\n\n    curl_close($ch);\n} else {\n    echo "Query parameter 'qu' is missing.";\n}\n?>