function luhnCheck($number) {\n    $number = str_replace(' ', '', $number); // Remove any spaces\n    $sum = 0;\n    $alt = false;\n    \n    for ($i = strlen($number) - 1; $i >= 0; $i--) {\n        $n = intval($number[$i]);\n        if ($alt) {\n            $n *= 2;\n            if ($n > 9) {\n                $n -= 9;\n            }\n        }\n        $sum += $n;\n        $alt = !$alt;\n    }\n    \n    return ($sum % 10 == 0);\n}\n\n$creditCardNumber = '4111111111111111'; // Example credit card number\nif (luhnCheck($creditCardNumber)) {\n    echo "Valid credit card number.";\n} else {\n    echo "Invalid credit card number.";\n}