function ajax_submit_comment() {\n    $comment_data = array(\n        'comment_post_ID' => intval($_POST['comment_post_ID']),\n        'comment_author' => sanitize_text_field($_POST['author']),\n        'comment_author_email' => sanitize_email($_POST['email']),\n        'comment_content' => sanitize_textarea_field($_POST['comment']),\n        'comment_type' => '',\n        'comment_parent' => intval($_POST['comment_parent']),\n        'user_id' => get_current_user_id(),\n    );\n\n    $comment_id = wp_insert_comment($comment_data);\n\n    if ($comment_id) {\n        wp_send_json_success('Comment submitted!');\n    } else {\n        wp_send_json_error('Error submitting comment.');\n    }\n}\nadd_action('wp_ajax_nopriv_ajax_submit_comment', 'ajax_submit_comment');\nadd_action('wp_ajax_ajax_submit_comment', 'ajax_submit_comment');