<?php\nclass Test {\n    function doAction() {\n        $this->doSubAction();\n    }\n    \n    function doSubAction() {\n        if (true) {\n            $backtrace = debug_backtrace();\n            $caller = $backtrace[1]; // Get the caller of the current method\n            $message = sprintf('Custom error in %s on line %d', $caller['file'], $caller['line']);\n            trigger_error($message, E_USER_WARNING);\n        }\n    }\n}\n\n$var = new Test();\n$var->doAction();\n?>