<?php\nrequire 'vendor/autoload.php';\n\nuse Carbon\Carbon;\n\nfunction getWorkdays($startDate, $endDate) {\n    $start = Carbon::parse($startDate);\n    $end = Carbon::parse($endDate);\n    $workdays = 0;\n\n    while ($start->lte($end)) {\n        if (!$start->isWeekend()) {\n            $workdays++;\n        }\n        $start->addDay();\n    }\n\n    return $workdays;\n}\n\n$startDate = '2008-08-26';\n$endDate = '2008-09-01';\necho "Total workdays: " . getWorkdays($startDate, $endDate);\n?>