<?php\nrequire_once('vendor/autoload.php');\n\nuse setasign\Fpdi\Fpdi;\n\n// Create new PDF document\n$pdf = new Fpdi();\n\n// Set the source file\n$pdf->setSourceFile('form.pdf');\n\n// Import the first page of the PDF\n$tplIdx = $pdf->importPage(1);\n\n// Add a page\n$pdf->AddPage();\n\n// Use the imported page as the template\n$pdf->useTemplate($tplIdx);\n\n// Set font and size\n$pdf->SetFont('Helvetica', '', 12);\n\n// Set the position for the form fields and add text\n$pdf->SetXY(50, 100);\n$pdf->Write(10, 'John Doe');\n\n$pdf->SetXY(50, 120);\n$pdf->Write(10, '123 Main St');\n\n// Flatten the PDF (optional, ensures the form fields are not editable)\n$pdf->Output('F', 'filled_form.pdf');\n\necho 'Form has been filled and saved as filled_form.pdf';\n?>