class third_party_library {\n    public function buggy_function() {\n        return 'bad result';\n    }\n    public function other_functions() {\n        return 'blah';\n    }\n}\n\nclass MyThirdPartyLibrary extends third_party_library {\n    public function buggy_function() {\n        return 'good result';\n    }\n}\n\n// Alias the new class to the original class name\nclass_alias('MyThirdPartyLibrary', 'third_party_library');\n\n// Testing the override\n$instance = new third_party_library();\necho $instance->buggy_function(); // Outputs: good result\necho $instance->other_functions(); // Outputs: blah