<?php\nfunction addIncludePaths(array $paths) {\n    foreach ($paths as $path) {\n        $realPath = realpath($path);\n        if ($realPath && is_dir($realPath)) {\n            $iterator = new RecursiveIteratorIterator(\n                new RecursiveDirectoryIterator($realPath, RecursiveDirectoryIterator::FOLLOW_SYMLINKS),\n                RecursiveIteratorIterator::SELF_FIRST\n            );\n            foreach ($iterator as $file) {\n                if ($file->isDir()) {\n                    set_include_path(get_include_path() . PATH_SEPARATOR . $file->getRealPath());\n                }\n            }\n        }\n    }\n}\n\n// Example usage:\n$directories = ['/path/to/first/dir', '/path/to/second/dir'];\naddIncludePaths($directories);\n?>