<?php\nsession_start();\n\nif (isset($_POST['username']) && isset($_POST['password'])) {\n    $username = $_POST['username'];\n    $password = $_POST['password'];  // You should hash this password before storing it or validating it\n    $_SESSION['username'] = $username;\n    $_SESSION['password'] = password_hash($password, PASSWORD_DEFAULT); // Passwords should be hashed\n}\n\nif (isset($_SESSION['username']) && isset($_SESSION['password'])) {\n    $navbar = "1";\n    $logindisplay = "0";\n    $username = $_SESSION['username'];\n    $hashed_password = $_SESSION['password']; // Password should be a hash\n} else {\n    header('Location: http://website.com/fail.php');\n    exit();\n}\n\nfunction auth($username, $password) {\n    // Fetch hashed password from your database\n    $stored_hashed_password = fetch_password_from_db($username);\n    \n    if ($stored_hashed_password && password_verify($password, $stored_hashed_password)) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\nif (!auth($username, $password)) {\n    header('Location: http://website.com/fail.php');\n    exit();\n}\n\n// Proceed with authenticated user\n?>