function getLeafNodes($array) {\n    $stack = [$array];\n    $leafNodes = [];\n\n    while (!empty($stack)) {\n        $current = array_pop($stack);\n\n        if (is_array($current)) {\n            foreach ($current as $element) {\n                if (is_array($element)) {\n                    $stack[] = $element;\n                } else {\n                    $leafNodes[] = $element;\n                }\n            }\n        }\n    }\n\n    return $leafNodes;\n}\n\n// Example usage:\n$inputArray = [\n    [\n        [\n            new Seller(5000000, 4998970, 0, 2, '2009-07-25 17:53:10')\n        ]\n    ],\n    [\n        [\n            new Seller(10000000, 9997940, 135, 234, '2009-07-14 23:36:00')\n        ]\n    ]\n];\n\n$leafNodes = getLeafNodes($inputArray);\nprint_r($leafNodes);\n\n// Assuming the Seller class is defined as:\nclass Seller {\n    private $credits;\n    private $balance;\n    private $queueid;\n    private $sellerid;\n    private $dateTime;\n\n    public function __construct($credits, $balance, $queueid, $sellerid, $dateTime) {\n        $this->credits = $credits;\n        $this->balance = $balance;\n        $this->queueid = $queueid;\n        $this->sellerid = $sellerid;\n        $this->dateTime = $dateTime;\n    }\n}