<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $input = $_POST['datetime'];\n\n    // Define the regular expression for the expected format\n    $regex = '/^(?<year>\d{4})(\/(?<month>\d{2}))?(\/(?<day>\d{2}))?(\/(?<hour>\d{2}))?(\/(?<minute>\d{2}))?(\/(?<second>\d{2}))?$/';\n\n    if (preg_match($regex, $input, $matches)) {\n        // Extract date components, providing default values if not set\n        $year = $matches['year'];\n        $month = isset($matches['month']) ? $matches['month'] : '01';\n        $day = isset($matches['day']) ? $matches['day'] : '01';\n        $hour = isset($matches['hour']) ? $matches['hour'] : '00';\n        $minute = isset($matches['minute']) ? $matches['minute'] : '00';\n        $second = isset($matches['second']) ? $matches['second'] : '01'; // Note: Second is defaulted to 01\n\n        // Validate and create the DateTime object\n        $datetime_string = "$year-$month-$day $hour:$minute:$second";\n        $datetime = DateTime::createFromFormat('Y-m-d H:i:s', $datetime_string);\n\n        if ($datetime && $datetime->format('Y-m-d H:i:s') === $datetime_string) {\n            echo "Valid DateTime: " . $datetime->format('Y-m-d H:i:s');\n            // Proceed with storing in the database\n        } else {\n            echo "Invalid DateTime format.";\n        }\n    } else {\n        echo "Invalid input format. Please use YYYY/MM/DD/HH/MM/SS.";\n    }\n}\n?>