<?php\n// Initialize cURL session\n$ch = curl_init("http://www.sportingpulse.com/rpt_ladder.cgi?results=N&round=15&client=1-3909-47801-81021-6151461&pool=-1");\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute cURL request\n$page = curl_exec($ch);\ncurl_close($ch);\n\n// Load the HTML content into DOMDocument\n$dom = new DOMDocument;\n@$dom->loadHTML($page); // Suppress warnings due to malformed HTML\n\n// Initialize DOMXPath\n$xpath = new DOMXPath($dom);\n\n// Query for the first table element\n$tableNodes = $xpath->query('//table');\n\n// Check if any table is found\nif ($tableNodes->length > 0) {\n    // Get the first table node\n    $table = $tableNodes->item(0);\n\n    // Save the table's HTML\n    $tableHTML = $dom->saveHTML($table);\n\n    // Output the table HTML\n    echo $tableHTML;\n} else {\n    echo "No table found.";\n}\n?>