<?php\n// Connect to your MySQL database\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Fetch the latest 25 codes ordered by datetime in descending order\n$query = "SELECT `code`, `datetime` FROM `fc` ORDER BY `datetime` DESC LIMIT 25";\n$result = $mysqli->query($query);\n\nif ($result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        $code = htmlspecialchars($row['code']);\n        $datetime = new DateTime($row['datetime']);\n        $now = new DateTime();\n        $interval = $now->diff($datetime);\n        \n        // Calculate the time difference\n        if ($interval->y > 0) {\n            $time_since = $interval->y . " Year(s) Ago";\n        } elseif ($interval->m > 0) {\n            $time_since = $interval->m . " Month(s) Ago";\n        } elseif ($interval->d > 0) {\n            $time_since = $interval->d . " Day(s) Ago";\n        } elseif ($interval->h > 0) {\n            $time_since = $interval->h . " Hour(s) Ago";\n        } elseif ($interval->i > 0) {\n            $time_since = $interval->i . " Minute(s) Ago";\n        } else {\n            $time_since = $interval->s . " Second(s) Ago";\n        }\n\n        // Generate the output\n        $output .= "<li><a href=\"http://www.example.com/?code=" . htmlspecialchars(urlencode($code)) . "\" target=\"_blank\">" . $code . " (" . $time_since . ")</a></li>";\n    }\n} else {\n    $output = "<p>No records found.</p>";\n}\n\necho $output;\n\n// Close the connection\n$mysqli->close();\n?>