function is_multidimensional_array($array) {\n    if (!is_array($array)) {\n        return false;\n    }\n    foreach ($array as $element) {\n        if (is_array($element)) {\n            return true;\n        }\n    }\n    return false;\n}\n\n// Example usage:\n$flatArray = [1, 2, 3, 4];\n$multiArray = [[1, 2], [3, 4]];\n\nvar_dump(is_multidimensional_array($flatArray)); // Output: bool(false)\nvar_dump(is_multidimensional_array($multiArray)); // Output: bool(true)