function bcceil($number) {\n    if (strpos($number, '.') !== false) {\n        list($intPart, $fracPart) = explode('.', $number, 2);\n        if (bccomp($fracPart, '0') > 0) {\n            return bcadd($intPart, '1', 0);\n        }\n        return $intPart;\n    }\n    return $number;\n}\n\nfunction bcfloor($number) {\n    if (strpos($number, '.') !== false) {\n        list($intPart, ) = explode('.', $number, 2);\n        return $intPart;\n    }\n    return $number;\n}\n\nfunction bcround($number, $precision = 0) {\n    if (strpos($number, '.') !== false) {\n        $pos = strpos($number, '.') + $precision + 1;\n        $digit = intval(substr($number, $pos, 1));\n        $number = bcadd($number, '0', $precision);\n        if ($digit >= 5) {\n            $factor = '1' . str_repeat('0', $precision);\n            $number = bcadd($number, bcdiv($factor, '10', $precision), $precision);\n        }\n        return $number;\n    }\n    return $number;\n}\n\n// Examples:\necho bcceil('31497230840470473074370324734723042.6') . "\n"; // 31497230840470473074370324734723043\necho bcfloor('31497230840470473074370324734723042.6') . "\n"; // 31497230840470473074370324734723042\necho bcround('31497230840470473074370324734723042.65', 0) . "\n"; // 31497230840470473074370324734723043\necho bcround('31497230840470473074370324734723042.65', 1) . "\n"; // 31497230840470473074370324734723042.7