function convert_smart_quotes($string) {\n    $search = array(\n        "\xE2\x80\x98", // ‘\n        "\xE2\x80\x99", // ’\n        "\xE2\x80\x9C", // “\n        "\xE2\x80\x9D", // ”\n        "\xE2\x80\x93", // –\n        "\xE2\x80\x94"  // —\n    );\n\n    $replace = array(\n        "'", // '\n        "'", // '\n        '"', // "\n        '"', // "\n        '-', // -\n        '-'  // -\n    );\n\n    return str_replace($search, $replace, $string);\n}\n\n// Example usage:\n$input = "This is an example with “smart quotes” and an em-dash —.";\n$output = convert_smart_quotes($input);\n\necho $output; // This is an example with "smart quotes" and an em-dash -.