<?php\nclass foo {\n  public $bar;\n  function __construct() {\n    echo "Foo Exists!";\n  }\n\n  function magic_bullet($id) {\n    switch($id) {\n    case 1:\n      echo "There is no spoon! ";\n      break; // Added break to prevent fallthrough\n    case 2:\n      echo "Or is there... ";\n      break;\n    }\n  }\n}\n\nclass bar {\n  private $foo;\n\n  function __construct($foo) {\n    $this->foo = $foo;\n    echo "Bar exists";\n  }\n\n  function target($id) {\n    echo "I want a magic bullet for this ID!";\n    $this->foo->magic_bullet($id);\n  }\n}\n\n$test = new foo();\n$test->bar = new bar($test);\n$test->bar->target(2);\n?>