<?php\n\nfunction checkUrl($url, $timeout = 10) {\n    $ch = curl_init();\n\n    // Set URL and other appropriate options\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_NOBODY, true); // We don't need the body\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true); // Return the transfer as a string\n    curl_setopt($ch, CURLOPT_TIMEOUT, $timeout); // Timeout in seconds\n    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true); // Follow redirects\n\n    // Execute the request\n    curl_exec($ch);\n\n    // Check if any error occurred\n    if (!curl_errno($ch)) {\n        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    } else {\n        $httpCode = 0; // Timeout or other errors\n    }\n\n    // Close handle\n    curl_close($ch);\n\n    // Return the HTTP status code\n    return $httpCode;\n}\n\nfunction checkUrls(array $urls, $timeout = 10) {\n    $results = [];\n\n    foreach ($urls as $url) {\n        $httpCode = checkUrl($url, $timeout);\n        $results[$url] = ($httpCode == 200);\n    }\n\n    return $results;\n}\n\n// Example usage\n$urls = [\n    "https://example.com",\n    "https://nonexistent.example.com",\n    // Add more URLs here\n];\n\n$results = checkUrls($urls);\nforeach ($results as $url => $isValid) {\n    echo "URL: $url is " . ($isValid ? "valid" : "invalid") . "\n";\n}\n?>