function getRootDomain($domain) {\n    // Regular expression to match the root domain with a 3-letter TLD\n    $pattern = "/([a-z0-9-]+\.[a-z]{3})$/i";\n    preg_match($pattern, $domain, $matches);\n    return $matches[1] ?? null;\n}\n\n// Test cases\n$domains = [\n    'here.example.com',\n    'example.com',\n    'example.org',\n    'here.example.org'\n];\n\nforeach ($domains as $domain) {\n    echo getRootDomain($domain) . PHP_EOL;\n}