<?php\n\nuse Drupal\Core\Routing\RouteMatchInterface;\nuse Symfony\Component\HttpFoundation\Response;\n\n/**\n * Implements hook_menu().\n */\nfunction custom_views_output_menu() {\n  $items = [];\n\n  $items['custom_views_output/%'] = [\n    'title' => 'Custom Views Output',\n    'page callback' => 'custom_views_output_view_page',\n    'page arguments' => [1],\n    'access callback' => TRUE,\n    'type' => MENU_CALLBACK,\n  ];\n\n  return $items;\n}\n\n/**\n * Custom page callback to render a view without page template.\n */\nfunction custom_views_output_view_page($view_name) {\n  // Load the view.\n  $view = \Drupal\views\Views::getView($view_name);\n  if ($view) {\n    // Execute the view.\n    $view->execute();\n    // Render the view.\n    $output = $view->render();\n    // Create a response object.\n    $response = new Response();\n    // Set the content of the response.\n    $response->setContent(render($output));\n    // Return the response without the page template.\n    return $response;\n  }\n  // Return a 404 response if the view is not found.\n  return new Response('View not found', 404);\n}