<?php\n\nnamespace Drupal\custom_views_output\Controller;\n\nuse Drupal\Core\Controller\ControllerBase;\nuse Symfony\Component\HttpFoundation\Response;\n\n/**\n * Controller for rendering views without page template.\n */\nclass CustomViewsOutputController extends ControllerBase {\n\n  /**\n   * Renders the specified view without the surrounding page template.\n   *\n   * @param string $view_name\n   *   The name of the view to render.\n   *\n   * @return \Symfony\Component\HttpFoundation\Response\n   *   The response containing the view output.\n   */\n  public function viewPage($view_name) {\n    // Load the view.\n    $view = \Drupal\views\Views::getView($view_name);\n    if ($view) {\n      // Execute the view.\n      $view->execute();\n      // Render the view.\n      $output = $view->render();\n      // Create a response object.\n      $response = new Response();\n      // Set the content of the response.\n      $response->setContent(render($output));\n      // Return the response without the page template.\n      return $response;\n    }\n    // Return a 404 response if the view is not found.\n    return new Response('View not found', 404);\n  }\n}