<?php\n$users = [\n    ['name' => 'Alice', 'pages_visited' => 10],\n    ['name' => 'Bob', 'pages_visited' => 8],\n    ['name' => 'Charlie', 'pages_visited' => 15],\n];\n\necho '<table border="1">';\necho '<tr><th>User</th><th>Pages Visited</th></tr>';\n\nforeach ($users as $user) {\n    echo '<tr>';\n    echo '<td>' . htmlspecialchars($user['name']) . '</td>';\n    echo '<td>' . htmlspecialchars($user['pages_visited']) . '</td>';\n    echo '</tr>';\n}\n\necho '</table>';\n?>